{
  description = "The Unified Swarm: One Environment to Rule Them All";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = import nixpkgs {
          inherit system;
          config.allowUnfree = true;
        };

        # ======================================================================
        # ⚠️  ARCHITECT'S NOTE TO FUTURE TRAVELERS (AI OR HUMAN)  ⚠️
        # ======================================================================
        # THIS FLAKE IMPLEMENTS "CLANG SUPREMACY".
        #
        # We enforce the use of `clangStdenv` and explicitly link `libclang` to 
        # reconcile the Rust and Elixir compilation toolchains. 
        #
        # DO NOT introduce GCC or standard `stdenv` overrides without understanding 
        # the delicate treaty between Rustler (NIFs) and the BEAM.
        #
        # Any change here must preserve:
        # 1. LIBCLANG_PATH export.
        # 2. BINDGEN_EXTRA_CLANG_ARGS with -isystem includes.
        # 3. The exclusion of conflicting C standard libraries.
        # ======================================================================

        # 1. Core BEAM Stack
        beamStack = with pkgs; [ elixir_1_18 erlang_27 rebar3 ];

        # 2. Scout & Archiver Tools
        scoutTools = with pkgs; [ 
          python3 
          python3Packages.tree-sitter
          # We provide the raw grammars here. The Python script will 
          # build the binaries from these sources.
          tree-sitter-grammars.tree-sitter-elixir
          tree-sitter-grammars.tree-sitter-rust

          python3Packages.pymavlink 
          python3Packages.onnx 
          python3Packages.onnxruntime 
          python3Packages.numpy 
          python3Packages.sympy
        ];

        # 3. Brain Tools (Aligned with Clang)
        brainTools = with pkgs; [
          libgphoto2 
          opencv4 
          pkg-config 
          cmake 
          libusb1 
          ffmpeg-full 
          gnumake
          libclang.lib
          cargo 
          rustc
        ];

      in {
        # CRITICAL: We override the standard environment with Clang.
        devShells.default = pkgs.mkShell.override { stdenv = pkgs.clangStdenv; } {
          name = "swarm-unified";

          nativeBuildInputs = [ 
            pkgs.libclang.lib 
            pkgs.pkg-config 
            pkgs.cmake 
          ]; 

          buildInputs = beamStack ++ scoutTools ++ brainTools;

          shellHook = ''
            export MIX_ENV=dev
            export NODE_NAME="$(hostname)"
            export EXLA_TARGET=host
            export XLA_TARGET=cpu
            export MIX_BUILD_PATH="$PWD/_build"
            export MIX_DEPS_PATH="$PWD/deps"

            # --- THE BUILDER'S BRIDGE (Clang Supremacy) ---
            export LIBCLANG_PATH="${pkgs.libclang.lib}/lib"

            # CRITICAL: Bindgen headers for Rust/C++ interop
            export BINDGEN_EXTRA_CLANG_ARGS=" \
              -isystem ${pkgs.llvmPackages.libclang.lib}/lib/clang/${pkgs.llvmPackages.libclang.version}/include \
              -isystem ${pkgs.glibc.dev}/include"

            # --- THE NEURO-SCANNER LINK (Tree-Sitter Source Paths) ---
            # We export the paths to the grammar SOURCES. 
            # The Python script will use these to compile the library on the fly.
            export TS_ELIXIR_SRC="${pkgs.tree-sitter-grammars.tree-sitter-elixir}"
            export TS_RUST_SRC="${pkgs.tree-sitter-grammars.tree-sitter-rust}"

            echo "🐝 Unified Swarm Environment Loaded"
            echo "   - Toolchain: Clang Supremacy (Verified)"
            echo "   - Context: Tree-Sitter Grammars Linked"
          '';
        };
      }
    );
}

use rustler::{ResourceArc, Binary, Env, Term, Atom, Encoder};
use std::sync::RwLock;
use image::{GrayImage, ImageBuffer, Luma};

mod atoms {
    rustler::atoms! {
        ok,
        error,
        no_change,
        change,
        invalid_shape
    }
}

struct RetinaState {
    // We use GrayImage (8-bit Luma) for maximum speed and compatibility
    last_frame: RwLock<Option<GrayImage>>,
    width: u32,
    height: u32,
    threshold: u32,
}

#[rustler::nif]
fn init_retina(width: u32, height: u32, threshold: u32) -> (Atom, ResourceArc<RetinaState>) {
    let state = RetinaState {
        last_frame: RwLock::new(None),
        width,
        height,
        threshold,
    };
    (atoms::ok(), ResourceArc::new(state))
}

#[rustler::nif]
fn detect_change<'a>(env: Env<'a>, resource: ResourceArc<RetinaState>, input: Binary<'a>) -> Term<'a> {
    let mut state = resource.last_frame.write().unwrap();

    // Safety: Ensure input binary matches expected dimensions
    if input.len() != (resource.width * resource.height) as usize {
        return (atoms::error(), atoms::invalid_shape()).encode(env);
    }

    // 1. Ingest: Zero-copy creation of the container (vectors own data)
    let current_bytes = input.as_slice().to_vec();
    let current_img = match GrayImage::from_raw(resource.width, resource.height, current_bytes) {
        Some(img) => img,
        None => return (atoms::error(), atoms::invalid_shape()).encode(env),
    };

    match &*state {
        Some(previous_img) => {
            // 2. Fast Entropy Calculation (SAD)
            // We iterate manually for SIMD-friendly linear scanning
            let sad: u32 = current_img.as_raw().iter()
                .zip(previous_img.as_raw().iter())
                .map(|(a, b)| (*a as i32 - *b as i32).abs() as u32)
                .sum();

            if sad < resource.threshold {
                *state = Some(current_img);
                atoms::no_change().encode(env)
            } else {
                // 3. ROI Calculation
                // Note: In the future, we can use imageproc::contours here!
                let (x, y, w, h) = calculate_roi(&current_img, previous_img);
                *state = Some(current_img);
                (atoms::change(), (x, y, w, h)).encode(env)
            }
        }
        None => {
            *state = Some(current_img);
            atoms::no_change().encode(env)
        }
    }
}

// Tactical ROI Scanner
fn calculate_roi(current: &GrayImage, previous: &GrayImage) -> (u32, u32, u32, u32) {
    let width = current.width();
    let height = current.height();
    let mut min_x = width;
    let mut max_x = 0;
    let mut min_y = height;
    let mut max_y = 0;

    let noise_gate = 15; 

    // Linear scan is cache-friendly
    for (i, (p1, p2)) in current.as_raw().iter().zip(previous.as_raw().iter()).enumerate() {
        let diff = (*p1 as i32 - *p2 as i32).abs();

        if diff > noise_gate {
            let x = (i as u32) % width;
            let y = (i as u32) / width;

            if x < min_x { min_x = x; }
            if x > max_x { max_x = x; }
            if y < min_y { min_y = y; }
            if y > max_y { max_y = y; }
        }
    }

    if max_x < min_x {
        return (0, 0, width, height);
    }

    (min_x, min_y, max_x - min_x, max_y - min_y)
}

rustler::init!("Elixir.SwarmBrain.Vision.Native", [init_retina, detect_change], load = on_load);

fn on_load(env: Env, _info: Term) -> bool {
    rustler::resource!(RetinaState, env);
    true
}

use rustler::{NifStruct, Atom, Term};

#[derive(NifStruct, Clone, Debug)]
#[module = "SwarmBrain.Vision.Box"]
pub struct Rect {
    pub x1: f32,
    pub y1: f32,
    pub x2: f32,
    pub y2: f32,
    pub score: f32,
    pub label: String,
}

#[rustler::nif]
pub fn nms(boxes: Vec<Rect>, iou_threshold: f32) -> Vec<Rect> {
    let mut detections = boxes;

    // Optimization: Sort Ascending so .pop() gives the highest score efficiently
    detections.sort_by(|a, b| a.score.partial_cmp(&b.score).unwrap_or(std::cmp::Ordering::Equal));

    let mut kept = Vec::with_capacity(detections.len());

    while let Some(best) = detections.pop() {
        kept.push(best.clone());

        // Remove any remaining box that overlaps too much with 'best'
        // We iterate backwards to safely remove items
        detections.retain(|item| calculate_iou(&best, item) < iou_threshold);
    }

    kept
}

fn calculate_iou(a: &Rect, b: &Rect) -> f32 {
    let x_left = a.x1.max(b.x1);
    let y_top = a.y1.max(b.y1);
    let x_right = a.x2.min(b.x2);
    let y_bottom = a.y2.min(b.y2);

    if x_right < x_left || y_bottom < y_top {
        return 0.0;
    }

    let intersection_area = (x_right - x_left) * (y_bottom - y_top);
    let area_a = (a.x2 - a.x1) * (a.y2 - a.y1);
    let area_b = (b.x2 - b.x1) * (b.y2 - b.y1);

    intersection_area / (area_a + area_b - intersection_area)
}

rustler::init!("Elixir.SwarmBrain.Vision.NMS", [nms]);

use rustler::{NifStruct, Env, Term, NifResult};
use std::path::Path;

#[rustler::nif(schedule = "DirtyCpu")]
fn load_model(path: String) -> NifResult<String> {
    if Path::new(&path).exists() {
        Ok(format!("🧠 Tactician Core online. Memory mapped: {}", path))
    } else {
        Ok(format!("❌ Error: Brain file not found at {}", path))
    }
}

#[rustler::nif(schedule = "DirtyCpu")]
fn think(context_text: String) -> String {
    // SIMULATED REASONING (The "Tube Amp" Warm-up)
    // This proves the pipeline works.

    if context_text.contains("person") {
        "DECISION: TRACK_TARGET. REASON: Unauthorized human detected."
    } else if context_text.contains("watch") {
        "DECISION: HOVER. REASON: High-value asset identified."
    } else {
        "DECISION: SEARCH. REASON: Sector clear."
    }.to_string()
}

rustler::init!("Elixir.SwarmBrain.Tactician.Native", [load_model, think]);

